import 'package:exhibition_task/utils/extensions.dart';
import 'package:exhibition_task/utils/text/header_1.dart';
import 'package:exhibition_task/utils/text/header_2.dart';
import 'package:exhibition_task/utils/text/normal_text.dart';
import 'package:exhibition_task/view_models/exhibit_view_model.dart';
import 'package:flutter/material.dart';
import 'package:provider/provider.dart';

import '../utils/custom_widgets/custom_textfield.dart';

class MyHomePage extends StatefulWidget {
  const MyHomePage({
    super.key,
  });

  @override
  State<MyHomePage> createState() => _MyHomePageState();
}

class _MyHomePageState extends State<MyHomePage> {
  @override
  void initState() {
    context.read<ExhibitionModel>().getExhibitList();
    super.initState();
  }

  TextEditingController searchController = TextEditingController();
  final scaffoldKey = GlobalKey<ScaffoldState>();

  @override
  Widget build(BuildContext context) {
    return SafeArea(
      child: Scaffold(
        key: scaffoldKey,
        // backgroundColor: Colors.lightBlue.withOpacity(0.9),
        body: Padding(
          padding: const EdgeInsets.only(top: 20, right: 20, left: 20),
          child: ListView(
            children: <Widget>[
              15.vSpace,
              HeaderOne(
                text: 'Exhibition List',
                textAlign: TextAlign.center,
                size: 24,
              ),
              CustomTextField(
                  textInputType: TextInputType.text,
                  textController: searchController,
                  autoFocus: false,
                  isPassword: false,
                  hintText: 'Search',
                  height: 52,
                  suffixIcon: const Icon(
                    Icons.search,
                    color: Colors.black54,
                  ),
